$(function() {
	// Adjustments for Safari on Mac
	if (navigator.userAgent.indexOf('Safari') != -1 && navigator.userAgent.indexOf('Mac') != -1 && navigator.userAgent.indexOf('AppleWebkit') == -1) {
        $('html').addClass('safari-mac'); // provide a class for the safari-mac specific css to filter with
    }

	loadAllPages();
	loadHomePage();
	loadProductPage();
	loadPostPage();
	loadOrderPage();
});

function loadAllPages() {
	// Lazy load image, optimize performace
    $('[data-lazy]').lazyload({
        event: 'scroll show slide',
        effect: 'fadeIn',
        threshold : 200,
		failure_limit: 9999,
		skip_invisible : false,
        data_attribute: 'lazy'
    });

	// Custom scroller
	if (!isMobilePhone()) {
		$('.scroller').each(function() {
			var scroller = $(this);
			scroller.mCustomScrollbar({
				axis: 'y',
				theme: 'minimal-dark',
				autoHideScrollbar: true,
				scrollbarPosition: 'outside',
				callbacks: {
					onScroll: function() {
						scroller.find('[data-lazy]').trigger('scroll');
					}
				}
			});
		});
	}

	// Scroll top button
	if (!isMobilePhone() && $('#pGoTop').length > 0) {
		$(window).bind('scroll', function() {
			// Wait for scroll end
			clearTimeout($.data(this, "scrollCheck"));
			$.data(this, "scrollCheck", setTimeout(function() {
				if ($(this).scrollTop() >= $(this).height()) {
					$('#pGoTop').fadeIn(200);
				} else {
					$('#pGoTop').fadeOut(200);
				}
			}, 250));
		});
		$('#pGoTop').click(function() {
			$('body, html').animate({ scrollTop: 0 }, 500);
			return false;
		});
	}

	// Top header scroll
	if (!isMobilePhone()) {
		var lastScrollT = 0;
		var setPageHeaderPos = function() {
			var windowT = $(window).scrollTop();
			var headerH = $('.pheader').height();
			if (windowT > headerH) {
				if (windowT > lastScrollT) {
					$('.pheader').css({ top: '-100%', opacity: 0 });
				}
				else {
					$('.pheader').css({ top: '-9.7rem', opacity: 1 });
				}
			}
			else if (windowT < headerH) {
				$('.pheader').css({ top: -windowT + 'px', opacity: 1 });
			}
			else {
				$('.pheader').css({ top: 0, opacity: 1 });
			}
			lastScrollT = windowT;
		};
		setTimeout(function() { setPageHeaderPos() }, 1000);
		$(window).scroll(function() {
			// clearTimeout($.data(this, 'scrollTimer'));
		    // $.data(this, 'scrollTimer', setTimeout(function() {
		    //     setPageHeaderPos();
		    // }, 250));
			setPageHeaderPos();
		});

		// Mega menu
		$('.mega-menu').hoverIntent({
			over: function() {
				$(this).find('.mega-drop').fadeToggle(200);
			},
			timeout: 100,
			interval: 250,
			selector: '.mega-wrap'
		});
	}

	if (isMobilePhone()) {
		var showSideMenu = function() {
			$('.pheader-mb').hide();
			$('.mobile-menu').show();
			$('.mobile-menu nav').animate({left: 0}, 250);
		};
		var hideSideMenu = function() {
			$('.mobile-menu nav').animate({left: "-85vw"}, 250, function() {
				$('.mobile-menu').hide();
				$('.pheader-mb').fadeIn(100);
			});
		}

		$('.pheader-mb .ph-btn-menu').click(function() {
			showSideMenu();
		});

		$('.mobile-menu .overlay').click(function(e) {
			hideSideMenu();
		});

		$('.mobile-menu a').each(function() {
			var menuItem = $(this);
			if (menuItem.find('.fa').length > 0) {
				menuItem.find('.fa').click(function(e) {
					menuItem.next('ul').slideToggle(250);
					menuItem.find('.fa').toggleClass('fa-caret-down')
										.toggleClass('fa-caret-up');
					e.preventDefault();
				});
			}
		});

		$('.mobile-menu ul:last-of-type a').click(function() {
			hideSideMenu();
		});

		$('.pheader-mb .ph-btn-search').click(function() {
			$('.pheader-mb .mode-1').hide();
			$('.pheader-mb .mode-2').fadeIn(100);
		});

		$('.pheader-mb .mode-2 .fa-times').click(function() {
			$('.pheader-mb .mode-2').hide();
			$('.pheader-mb .mode-1').fadeIn(100);
		});

		$('.category-menu a').each(function() {
			var link = $(this);
			if (link.next('ul').length > 0) {
				link.find('.fa').click(function(e) {
					link.next('ul').slideToggle(200);
					e.preventDefault();
				});
			}
		});
	}

	// Banner video / Youtube
	$('.banner-video').fancybox({
		openEffect: 'none',
		closeEffect: 'none',
		helpers : {
			media : {},
			overlay: {
				locked: false
			}
		}
	});

	// Banner rotator
	var setRotatorItv = function(banRot, banLst) {
		var banAtv = '',
			banIdx = 0,
			banNdx = 0,
			banSpd = parseInt(banRot.data('speed')),
			banDur = parseInt(banRot.data('duration'));
		return setInterval(function() {
			banAtv = banRot.find('.active');
			banIdx = banLst.index(banAtv);
			// Infinite rotation
			if (banIdx == banLst.length - 1) {
				banIdx = 0;
			}
			else {
				banIdx++;
			}
			banLst.hide().removeClass('active');
			banLst.eq(banIdx).fadeIn(banSpd, function() {
				$(this).addClass('active');
			})
			.css('display', 'block');
		}, banDur);
	};
	$('.banner-rotator').each(function() {
		var banRot = $(this);
		var banLst = banRot.find('.banner-photo, .banner-video');
		var banHov = parseBool(banRot.data('hover'));
		if (banLst.length > 1) {
			var banItv = setRotatorItv(banRot, banLst);
			if (banHov) {
				banRot.hover(function() {
					banItv = clearInterval(banItv);
				},
				function() {
					banItv = setRotatorItv(banRot, banLst);
				});
			}
		}
	});

	// Banner slider
	$('.banner-slider').each(function() {
		var banSld = $(this);
		var banRnn = banSld.find('.banner-runner');
		var banPgr = banSld.find('.banner-pager');
		var banHov = parseBool(banSld.data('hover'));
		var banIdx = parseInt(banSld.data('index'));
		var banSpd = parseInt(banSld.data('speed'));
		var banDur = parseInt(banSld.data('duration'));
		banSld.waitForImages(function() {
			banRnn.bxSlider({
				auto: true,
				pause: banDur,
				speed: banSpd,
				captions: false,
				autoHover: banHov,
				startSlide: banIdx,
				pagerCustom: banPgr,
				easing: 'ease-in-out',
				controls: isMobilePhone(),
				onSliderLoad: function(slider) {
					// Fix cloned slides lazy image
					banRnn.find('.bx-clone .imgh').each(function() {
						var clonedImgh = $(this);
						clonedImgh.css("background-image", "url('" + clonedImgh.attr('data-lazy') + "')");
					});
				},
		        onSlideAfter: function() {
					// Load slide image on slide event
		            banRnn.trigger('slide');
		        }
			});
		});
	});

	// Quickview slider
	var setQvSlider = function(slider) {
		if (slider.length == 0 || slider.hasClass('loaded')) {
			return;
		}
		// Get slides config
		var dSlides = parseInt(slider.data("dcol"));
		var mSlides = parseInt(slider.data("mcol"));
		// Setup slider
		slider.bxSlider({
			pager: false,
			slideMargin: 20,
			slideWidth: 9999,
			moveSlides: mSlides,
			minSlides: isMobilePhone() ? mSlides : dSlides,
			maxSlides: isMobilePhone() ? mSlides : dSlides,
			onSliderLoad: function(slider) {
				// Mark as loaded
				slider.viewport.find('.slider').addClass('loaded');
				// Fix cloned slides lazy image
				slider.viewport.find('.bx-clone .imgh').each(function() {
					var clonedImgh = $(this);
					clonedImgh.css("background-image", "url('" + clonedImgh.attr('data-lazy') + "')");
				});
			},
	        onSlideAfter: function() {
				// Load slide image on slide event
	            slider.trigger('slide');
	        }
		});
	};
	setQvSlider($('.quick-view .tab-pane.active .slider'));
	$('.quick-view a[data-toggle="tab"]').on('shown.bs.tab', function (e) {
		var qvTab = $($(e.target).attr('href'));
		var qvSld = qvTab.find('.slider');
		if (qvSld.length > 0) {
			setQvSlider(qvSld);
		}
		else {
			qvTab.trigger('slide');
		}
	});

	// Range slider
	$('.range-input').each(function() {
		var inputRange = $(this);
		inputRange.slider({
			tooltip: 'always',
			tooltip_split: true,
			tooltip_position: 'bottom',
			value: [
				parseInt(inputRange.data("slider-min")),
				parseInt(inputRange.data("slider-max"))
			],
			formatter: function(value) {
				return isNaN(value)
					? value
					: formatNumber(value) + inputRange.data("formatter-unit");
			}
		});
	});

	// Validate form
	$('#signinForm, #signupForm, #forgotPwdForm, #changePwdForm, #userInfoForm').each(function() {
		$(this).validate({
			errorElement: 'em',
			errorPlacement: function(error, element) {
				error.addClass('form-message').insertAfter(element.next('span'));
			}
		});
	});

	// Show popover
	$('#uiAccountBtn, #cartBtn').each(function() {
		var target = $(this);
		target.hoverIntent({
			over: function() {
				target.find('.popover').fadeToggle(200);
			},
			timeout: 100,
			interval: 250
		});
	});

	// Cart modal
	var cartModalTm;
	var dismissCartModal = function() {
		cartModalTm = setTimeout(function() {
			$('#cartModal').modal('hide');
		}, 25000);
	}
	$('#cartModal').on('shown.bs.modal', function() {
		dismissCartModal();
	})
	.hover(function() {
		cartModalTm = clearTimeout(cartModalTm);
	}, function() {
		dismissCartModal();
	});
	
	if (isMobilePhone()) {
		// Width of the reCAPTCHA element, in pixels
		var reCaptchaWidth = 304;
		// Get the containing element's width
		var containerWidth = $('.grid').width() * .91;
		
		// Only scale the reCAPTCHA if it won't fit
		// inside the container
		if(reCaptchaWidth > containerWidth) {
			// Calculate the scale
			var captchaScale = containerWidth / reCaptchaWidth;
			// Apply the transformation
			$('.modal-body .g-recaptcha').css({
				'transform': 'scale('+captchaScale+')'
			});
		}
	}
}

